mapper.select:
SELECT a.attendance_id as attendance_id, a.attendance_user_id as attendance_user_id, u.user_name as userName, d.depart_name as departName, a.time_in as time_in, a.time_out as time_out, a.date as date, a.status as status, a.address as address, a.type as type FROM user as u INNER JOIN depart as d ON u.department_id = d.depart_id INNER JOIN attendance as a ON a.attendance_user_id = u.user_id
AND a.attendance_id = #{attendanceId}
AND a.attendance_user_id = #{attendanceUserId}
AND u.user_name LIKE CONCAT('%', #{userName}, '%')
AND d.depart_name LIKE CONCAT('%', #{departName}, '%')
AND a.date = #{date}
AND a.status = #{status}
AND a.address LIKE CONCAT('%', #{address}, '%')
AND a.type = #{type}

SELECT u.user_name
FROM user as u
WHERE u.user_id = #{attendanceId}

select *
from attendance
where
  attendance_user_id = #{userId}
  
      and  year(date) = #{year}
  
  
    and month(date) = #{month}
