mapper.select:
select v.id, v.field_id, f.field_title, f.field_type, f.field_sort, f.validation_rule, f.is_required, f.form_item_type, f.default_value, f.is_summary, f.is_visible, v.field_value from form_template_field f left join form_field_value v on f.field_id = v.field_id
v.form_id = #{formId}
order by f.field_sort asc
