mapper.select:
SELECT r.report_id AS report_id, r.report_user_id AS report_user_id, r.report_name AS report_name, r.type AS type, r.report_date AS report_date, r.up_date AS up_date, r.ct_date AS ct_date, r.file_path AS file_path, r.content AS content, u.user_name AS userName, d.depart_name AS departName FROM report r LEFT JOIN user u ON r.report_user_id = u.user_id LEFT JOIN depart d ON u.department_id = d.depart_id
AND r.report_name = #{reportName}
AND u.user_name = #{userName}
AND d.depart_name = #{departName}
AND r.content LIKE CONCAT('%', #{content}, '%')
AND r.report_date = #{reportDate}
